    def FollowAnchor(self):

        if self["关注"].wait_for_visible(10, raise_error = False):
            self["关注"].click()
            time.sleep(4)
            if self["粉丝团入口"].wait_for_visible(raise_error=False):
                return 0
            else:
                self["关注"].wait_for_visible(raise_error=False)
                self["关注"].click()
                return 0
        else:
            self.SwipeToNextRoom()
            while self["粉丝团入口"].visible:
                self.SwipeToNextRoom()
            time.sleep(5)
            if self["关注"].visible:
                self["关注"].click()
                time.sleep(2)
                if self["粉丝团入口"].wait_for_visible(raise_error=False):
                    return 0
                else:
                    self["关注"].wait_for_visible(raise_error=False)
                    self["关注"].click()
                    return 0
            else:
                return 1

