        time.sleep(3)
        tab_center = self[title].rect.center
        logger.info("点击tab前，accessibility元素坐标：%s" % self[title].rect)
        self.app.click(tab_center[0], tab_center[1])
        logger.info("点击tab后，accessibility元素坐标：%s" % self[title].rect)
        self[title].click()